import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import './ImDrinking.css';
import Navbar from '../components/Navbar';

const drinkOptions = [
  "Jack Daniel's Old No. 7 Tennessee Whiskey",
  "Jack Daniel's Country Cocktail",
  "Jameson Orange Spirit Drink",
  "Smirnoff Ice",
  "High Noon Vodka Seltzer",
  "Jameson Black Barrel",
  "Fireball Cinnamon Whiskey",
  "Twisted Tea Hard Iced Tea",
  "White Claw Hard Seltzer",
  "Dogfish Head Cocktail",
  "Coors Light Beer Can",
  "Bud Light Beer Bottle",
  "Corona Extra Beer Bottle",
  "Angry Orchard Hard Cider Bottle",
  "Casamigos Blanco Tequila",
  "JINRO Chamisul Soju Fresh",
  "New Amsterdam Pink Whitney",
  "Malibu Caribbean Coconut Liquor Rum",
  "Tito's Handmade Vodka",
  "Heineken Lager"
];

export default function ImDrinking() {
  const [selectedDrink, setSelectedDrink] = useState('');
  const [bodyInfo, setBodyInfo] = useState('');
  const [mixes, setMixes] = useState([{ drink: '', percentage: '' }, { drink: '', percentage: '' }]);
  const navigate = useNavigate();

  const handleDrinkChange = (e) => {
    const drink = e.target.value;
    setSelectedDrink(drink);
    // For now, simulate personalized info (replace with your own calculations)
    setBodyInfo(`Personalized info for ${drink}: [Metabolic and tolerance details here]`);
  };

  const handleMixChange = (index, field, value) => {
    const newMixes = [...mixes];
    newMixes[index][field] = value;
    setMixes(newMixes);
  };

  const addMix = () => {
    setMixes([...mixes, { drink: '', percentage: '' }]);
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    // You might pass the selected drink and mixes to the diagram page via query params or state.
    if (selectedDrink.trim() === '') return;
    navigate(`/drink-diagram?drink=${encodeURIComponent(selectedDrink)}`);
  };

  return (
    <div className="im-drinking-container">
      <Navbar onSignOut={() => {
        localStorage.removeItem('bacshotsUser');
        window.location.href = '/signin';
      }} />
      <div className="im-drinking-content">
        <h1>I'm drinking</h1>
        <div className="drink-select-wrapper">
          <select 
            className="custom-dropdown"
            value={selectedDrink} 
            onChange={handleDrinkChange}
          >
            <option value="" disabled>Select a drink</option>
            {drinkOptions.map((drink, idx) => (
              <option key={idx} value={drink}>{drink}</option>
            ))}
          </select>
        </div>
        {bodyInfo && (
          <div className="body-info">
            {bodyInfo}
          </div>
        )}
        <div className="mixes-section">
          <h2>Mixes</h2>
          {mixes.map((mix, index) => (
            <div key={index} className="mix-item">
              <select
                className="custom-dropdown mix-dropdown"
                value={mix.drink}
                onChange={(e) => handleMixChange(index, 'drink', e.target.value)}
              >
                <option value="" disabled>Select a drink</option>
                {drinkOptions.map((drink, idx) => (
                  <option key={idx} value={drink}>{drink}</option>
                ))}
              </select>
              <input
                type="number"
                className="mix-percentage"
                placeholder="Percentage"
                value={mix.percentage}
                onChange={(e) => handleMixChange(index, 'percentage', e.target.value)}
              />
            </div>
          ))}
          <button className="add-mix-btn" onClick={addMix}>+</button>
        </div>
        <button className="submit-btn" onClick={handleSubmit}>
          Next &gt;
        </button>
      </div>
    </div>
  );
}
