// client/src/pages/ImDrinking.js
import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import './ImDrinking.css';
import Navbar from '../components/Navbar';

// For demonstration, we load a static list.
// In a real app, you could fetch these from an API.
const drinkOptions = [
  "Jack Daniel's Old No. 7 Tennessee Whiskey",
  "Jack Daniel's Country Cocktail",
  "Jameson Orange Spirit Drink",
  "Smirnoff Ice",
  "High Noon Vodka Seltzer",
  "Jameson Black Barrel",
  "Fireball Cinnamon Whiskey",
  "Twisted Tea Hard Iced Tea",
  "White Claw Hard Seltzer",
  "Dogfish Head Cocktail",
  "Coors Light Beer Can",
  "Bud Light Beer Bottle",
  "Corona Extra Beer Bottle",
  "Angry Orchard Hard Cider Bottle",
  "Casamigos Blanco Tequila",
  "JINRO Chamisul Soju Fresh",
  "New Amsterdam Pink Whitney",
  "Malibu Caribbean Coconut Liquor Rum",
  "Tito's Handmade Vodka",
  "Heineken Lager"
];

export default function ImDrinking() {
  const [selectedDrink, setSelectedDrink] = useState('');
  const navigate = useNavigate();

  const handleSelectChange = (e) => {
    setSelectedDrink(e.target.value);
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    // Navigate to the diagram page with the selected drink as a query parameter.
    // You could pass more than one drink later.
    if (selectedDrink.trim() === '') return;
    navigate(`/drink-diagram?drink=${encodeURIComponent(selectedDrink)}`);
  };

  return (
    <div className="im-drinking-container">
      <Navbar onSignOut={() => {
        localStorage.removeItem('bacshotsUser');
        window.location.href = '/signin';
      }} />
      <div className="im-drinking-content">
        <h1>I'm drinking</h1>
        <form onSubmit={handleSubmit}>
          <input 
            list="drink-list" 
            placeholder="Type or press 'v' for options..." 
            value={selectedDrink} 
            onChange={handleSelectChange} 
            className="drink-input"
          />
          <datalist id="drink-list">
            {drinkOptions.map((drink, idx) => (
              <option key={idx} value={drink} />
            ))}
          </datalist>
          <button type="submit" className="submit-btn">Tonight</button>
        </form>
      </div>
    </div>
  );
}
